        public static bool IsDigitsOnly(string str)
        {
            string str2 = str;
            int num = 0;
            int length = str2.Length;
            while (num < length)
            {
                char ch = str2[num];
                if ((ch < '0') || (ch > '9'))
                {
                    return false;
                }
                num++;
            }
            return true;
        }

